use IO::Socket;
$server_con = new IO::Socket::INET (  
	   PeerAddr => 'localhost',
	   PeerPort => 6001,
	   Proto    => 'tcp'
     			) or die "could not open socket $!";

$mess = "";
			
while(1)
{
	$record_time = localtime;
	$len = length($mess);
	$p = pack "N", $len;
	$b = length $p;
	#print "$len $b #$p\n";
	print $server_con $p;

	print $server_con "$mess";

	#print $server_con "";
	print $record_time,"\n";
	sleep 3;

}




