use IO::Socket;
$server_con = new IO::Socket::INET (  
	   PeerAddr => 'localhost',
	   PeerPort => 6001,
	   Proto    => 'tcp'
     			) or die "could not open socket $!";

$mess = "\n";
			
while(1)
{
	$record_time = localtime;
	print $server_con "$mess";

	#print $server_con "";
	print $record_time,"\n";
	sleep 3;

}




